export default {
  data() {
    return {
      activeTab: null,
      tabs: ['Заявки', 'Счета'],
      // Массив табов
      texts: {
        // Тексты для каждого таба
        'Заявки': 'Содержимое таба "Заявки"',
        'Счета': 'Содержимое таба "Счета"'
      }
    };
  },
  methods: {
    getText(tabName) {
      console.log('Getting text for tab:', tabName);
      return this.texts[tabName];
    }
  },
  mounted() {
    this.tabs.forEach(tabName => {
      console.log('Calling getText for tab:', tabName);
      console.log('Text for', tabName, ':', this.getText(tabName));
    });
  }
};
