import Vue from 'vue';
import Vuex from 'vuex';
import axios from 'axios';

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    orderData: null
  },
  mutations: {
    setOrderData(state, data) {
      state.orderData = data;
    }
  },
  actions: {
    fetchOrderData({ commit }) {
      axios.get('https://my-json-server.typicode.com/plushevy/demo/orders/1')
        .then(response => {
          commit('setOrderData', response.data);
        })
        .catch(error => {
          console.error('Ошибка при загрузке данных заявки:', error);
        });
    }
  }
});
